    {
        Name = "Integration service token",
        InlineRoles = new[]
        {
            new Launchdarkly.Inputs.AccessTokenInlineRoleArgs
            {
                Actions = new[]
                {
                    "*",
                },
                Effect = "deny",
                Resources = new[]
                {
                    "proj/*:env/production",
                },
            },
        },
        ServiceToken = true,
    });

});
